import tn.esprit.financialhub.models.Reclamation;
import tn.esprit.financialhub.utils.MyDatabase;

import java.sql.*;
import java.time.LocalDate;
import java.util.ArrayList;
import java.util.List;

public class ReclamationService implements IService&lt;Reclamation&gt; {


    private  Connection connection ;
    public ReclamationService(){
        connection = MyDatabase.getInstance().getConnection();
    }
    @Override
    public  void ajouter(Reclamation reclamation) throws SQLException {
        String req = &quot;INSERT INTO reclamation(Username, Email, Description, Etat, Type) VALUES('&quot; + reclamation.getUsername() + &quot;', '&quot; + reclamation.getEmail() + &quot;', '&quot; + reclamation.getDescription() + &quot;', '&quot; + reclamation.getEtat() + &quot;', '&quot; + reclamation.getType() + &quot;')&quot;;
        Statement st = connection.createStatement();
        st.executeUpdate(req);
    }

    @Override
    public void modifier(Reclamation reclamation) throws SQLException {
        String req =&quot;UPDATE reclamation SET Username = ? , Email = ? , Description_reclamation = ? , Etat = ? , Type=?&quot;;
        PreparedStatement ps = connection.prepareStatement(req);
        ps.setString(1,reclamation.getUsername());
        ps.setString(2,reclamation.getEmail());
        ps.setString(3,reclamation.getDescription());
        ps.setString(4,reclamation.getEtat());
        ps.setString(5,reclamation.getType());

        ps.executeUpdate();


    }

    @Override
    public void supprimer(Reclamation reclamation) throws SQLException {
        String req = &quot;DELETE FROM reclamation WHERE id = ?&quot;;
        PreparedStatement ps = connection.prepareStatement(req);
        ps.setInt(1, reclamation.getId());
        ps.executeUpdate();
    }


    public List&lt;Reclamation&gt; recuperer() throws SQLException {
        List&lt;Reclamation&gt; reclamations = new ArrayList&lt;&gt;();
        String req = &quot;SELECT * FROM reclamation&quot;;
        Statement st = connection.createStatement();
        ResultSet rs = st.executeQuery(req);

        while (rs.next()) {
            Reclamation reclamation = new Reclamation();
            reclamation.setId(rs.getInt(&quot;id&quot;));
            reclamation.setUsername(rs.getString(&quot;Username&quot;));
            reclamation.setEmail(rs.getString(&quot;Email&quot;));
            reclamation.setDescription(rs.getString(&quot;Description&quot;));
            reclamation.setType(rs.getString(&quot;Type&quot;));
            reclamation.setEtat(rs.getString(&quot;Etat&quot;));
            reclamation.setDate(rs.getDate(&quot;Date&quot;).toLocalDate());

            reclamations.add(reclamation);
        }

        return reclamations;
    }


    public void repondre(Reclamation reclamation) {
    }
}
